import numpy as np
import ctypes
from OpenGL import GL as pygl

from PySide6.QtOpenGL import QOpenGLBuffer, QOpenGLVertexArrayObject
from PySide6.QtGui import QMatrix4x4, QQuaternion, QVector4D, QVector3D

from PySide6.support import VoidPtr

class Model:
    funcs = None
    def __init__(self, id):
        self.id = id
        self.children = []
        self.parent = None
        self.type = ""
        self.name = ""
        self.position = QVector3D(0, 0, 0)
        self.quaternion = QQuaternion()
        self.color = QVector4D(0.3, 0.3, 0.3, 1.0)
        self.data = None
        self.vao = QOpenGLVertexArrayObject()
        self.vbo = QOpenGLBuffer(QOpenGLBuffer.VertexBuffer)
        self.isBound = False
        self.calculateModelMatrix()

    def initialize(self, funcs, force = False):
        if self.isBound and force == False:
          return
        self.funcs = funcs
        self.vao.create()
        vaoBinder = QOpenGLVertexArrayObject.Binder(self.vao)

        self.vbo.create()
        self.vbo.bind()
        floatSize = ctypes.sizeof(ctypes.c_float)
        self.vbo.allocate(
            self.data.tobytes(),
            floatSize * self.data.size)

        funcs.glEnableVertexAttribArray(0)  # viewport
        funcs.glVertexAttribPointer(0,
                                    3,
                                    int(pygl.GL_FLOAT),
                                    int(pygl.GL_FALSE),
                                    6 * floatSize,
                                    VoidPtr(0)
                                    )
        funcs.glEnableVertexAttribArray(1)
        funcs.glVertexAttribPointer(1,
                                    3,
                                    int(pygl.GL_FLOAT),
                                    int(pygl.GL_FALSE),
                                    6 * floatSize,
                                    VoidPtr(3 * floatSize)
                                    )
        self.isBound = True

    def bind(self):
        self.vao.bind()
        self.vbo.bind()
    
    def draw(self, funcs):
      funcs.glDrawArrays(pygl.GL_TRIANGLES, 0, 36)
        
    def release(self):
        self.vbo.release()
        vaoBinder = None

    def destroy(self):
        self.vbo.destroy()
        self.vao.destroy()
        for i, child in enumerate(self.children):
            child.destroy()

    def value(self):
        return {}

    def calculateModelMatrix(self):
        self.modelMatrix = QMatrix4x4()
        self.modelMatrix.translate(self.position)
        self.modelMatrix.rotate(self.quaternion)

  
    def setColor(self, r, g, b, a):
        self.color.setX(r)
        self.color.setY(g)
        self.color.setZ(b)
        self.color.setW(a)

    def setPosition(self, x, y, z):
        self.position.setX(x)
        self.position.setY(y)
        self.position.setZ(z)
        self.calculateModelMatrix()
    
    def setPositionX(self, value):
        self.position.setX(value)
        self.calculateModelMatrix()
    
    def setPositionY(self, value):
        self.position.setY(value)
        self.calculateModelMatrix()
    
    def setPositionZ(self, value):
        self.position.setZ(value)
        self.calculateModelMatrix()

    def setQuaternion(self, x, y, z, w):
        self.quaternion.setX(x)
        self.quaternion.setY(y)
        self.quaternion.setZ(z)
        self.quaternion.setScalar(w)
        self.calculateModelMatrix()

    def setQuaternionX(self, value):
        self.quaternion.setX(value)
        self.calculateModelMatrix()
    
    def setQuaternionY(self, value):
        self.quaternion.setY(value)
        self.calculateModelMatrix()
    
    def setQuaternionZ(self, value):
        self.quaternion.setZ(value)
        self.calculateModelMatrix()
    
    def setQuaternionW(self, value):
        self.quaternion.setScalar(value)
        self.calculateModelMatrix()
    
    def setName(self, name):
        self.name = name



